        img(src='@/assets/curso/temas/tema5/img-19.png' style="max-width: 210px").col-xl-4.mb-4.mb-xl-auto.mb-xl-0.mx-auto.me-xl-3
        p.col-xl-8.mx-auto <b>Niice</b>
          br
